import React, { useState } from "react";
import MainPage from "./MainPage";
import SinglePlayer from "./SinglePlayer";

const PlayerCard = (props) => {
  const [playerInfo, setPlayerInfo] = useState(false);
  //   const detailButtonHandler = (data, e) => {
  //     return <SinglePlayer data={data} />;
  //   };

  return (
    <div id="all-players-container">
      {props.allPlayers.map((player, index) => {
        // <div key={index}>{player.name}</div>
        return (
          <div className="single-player-card" key={index}>
            <div className="header-info">
              <p className="pup-title">{player.name}</p>
              <p className="pup-number">#{player.id}</p>
            </div>
            <img src={player.imageUrl} alt="photo of the puppy" />
            <button
              className="detail-button"
              data-id={player.id}
              onClick={() => {
                return <SinglePlayer player={player} />;
              }}
            >
              See details
            </button>
            <button className="remove-player" data-id={player.id}>
              Remove Player
            </button>
          </div>
        );
      })}
    </div>
  );
};

export default PlayerCard;
