import React, { useState, useEffect } from "react";

const FetchAllPlayers = () => {
  const [allPlayers, setAllPlayers] = useState("");
  const getAllPlayers = async () => {
    try {
      const response = await fetch(
        "https://fsa-puppy-bowl.herokuapp.com/api/2211-FTB-ET-WEB-AM/players"
      );
      const result = await response.json();
      const playersArr = result.data.players;
      setAllPlayers(playersArr);

      if (result.error) {
        throw result.error;
      }
      return result.data.players;
    } catch (error) {
      console.log("Uh oh, trouble fetching players!", error);
    }
  };

  useEffect(() => {
    getAllPlayers();
  }, []);

  console.log(allPlayers);
  {
    allPlayers.map((player) => {
      return console.log(player);
    });
  }

  return (
    <div id="all-players-container">
      <div className="single-player-card">
        <div className="header-info">
          <p className="pup-title">{player.name}</p>
          <p className="pup-number">#{player.id}</p>
        </div>
        <img src={player.imageUrl} alt="photo of name the puppy" />
        <button className="detail-button" data-id={player.id}>
          See details
        </button>
        <button className="remove-player" data-id={player.id}>
          Remove Player
        </button>
      </div>
    </div>
  );
};

export default FetchAllPlayers;
